  `desc` varchar(255) DEFAULT '' COMMENT '分组描述',
  `status` tinyint(1) DEFAULT '1' COMMENT '状态(1显示0隐藏)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='[api] 应用分组';

