# Dump of table hisiphp_api_doc
# ------------------------------------------------------------

DROP TABLE IF EXISTS `hisiphp_api_doc`;

CREATE TABLE `hisiphp_api_doc` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `category_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '分类ID',
  `title` varchar(200) NOT NULL DEFAULT '' COMMENT '文档标题',
  `content` text NOT NULL COMMENT '文档详情',
  `sort` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态(1显示，0隐藏)',
  `views` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览统计',
  `ctime` int(11) unsigned NOT NULL DEFAULT '0',
  `mtime` int(11) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;



INSERT INTO `hisiphp_api_doc` (`id`, `category_id`, `title`, `content`, `sort`, `status`, `views`, `ctime`, `mtime`)
VALUES
	(1,14,'登录授权','&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;-授权流程说明&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;OAuth2.0授权登录让用户使用HisiPHP身份安全登录第三方应用或网站，在HisiPHP用户授权登录已接入微信OAuth2.0的第三方应用后，第三方可以获取到用户的接口调用凭证（User-Token），通过User-Token可以进行HisiPHP开放平台授权关系接口调用，从而可实现获取HisiPHP用户基本开放信息和帮助用户实现基础开放功能等。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;OAuth2.0授权登录目前支持authorization_code模式，适用于拥有server端的应用授权。该模式整体流程为：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;1. 第三方发起授权登录请求，HisiPHP用户允许授权第三方应用后，HisiPHP会拉起应用或重定向到第三方网站，并且带上授权临时票据code参数；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;2. 通过code参数加上AppID和AppSecret等，通过API换取User-Token；&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;3. 通过User-Token进行接口调用，获取用户基本数据资源或帮助用户实现基本操作。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;-第一步：请求CODE&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;请求方式：GET&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;请求地址：&lt;a href=&quot;http://api.hisiphp.com/oauth/authorize&quot;&gt;http://api.hisiphp.com/oauth/authorize&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;strong&gt;&amp;nbsp; &amp;nbsp; 请求参数：&lt;/strong&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;参数&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; width=&quot;118&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;strong&gt;&amp;nbsp; &amp;nbsp; &lt;/strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;是否必须&lt;/span&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/strong&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;说明&lt;/span&gt;&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;app_id&lt;/span&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; width=&quot;118&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;是&lt;/span&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;应用唯一标识&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;response_type&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; width=&quot;118&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;是&lt;/span&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; align=&quot;center&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;固定值：code&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;middle&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; align=&quot;center&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;redirect_uri&lt;/span&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; align=&quot;center&quot; width=&quot;118&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;是&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;td valign=&quot;middle&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; align=&quot;center&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;授权地址，请使用urlEncode对链接进行处理&amp;nbsp;&amp;nbsp;&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;middle&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; align=&quot;center&quot; style=&quot;word-break: break-all;height:30px;border-color: rgb(221, 221, 221); border-width: 1px; border-style: solid;&quot;&gt;&lt;span style=&quot;font-si